mapper.select:
SELECT
    user_id userId,
    role_id roleId
FROM eam_user
WHERE user_account = #{userAccount}
AND user_pwd = #{userPwd}

SELECT MI.menu_id menuId, MI.menu_name menuName, MI.menu_url menuUrl, MI.menu_f_id menufId, MI.order_num orderNum, MI.menu_icon menuIcon FROM eam_user_role_info URI INNER JOIN eam_user_menurole_link UML ON URI.role_id = UML.role_id INNER JOIN eam_menu_info MI ON MI.menu_id = UML.menu_id WHERE URI.role_id = #{roleId}
AND MI.menu_f_id = #{menuId}
AND MI.menu_f_id IS NULL
ORDER BY MI.order_num
